import React, { useState } from 'react';

function Login(props) {
    const username = useFormInput('');
    const password = useFormInput('');


    const handleLogin = () => {
        props.history.push('/dashboard');
    }
    const useFormInput = initialValue => {
        const [value, setValue] = useState(initialValue);

        const handleChange = e => {
            setValue(e.target.value);
        }
        return {
            value,
            onChange: handleChange
        }
    }

    return (
        <div className="" >
            <div >
                <div className="fullName" >
                    <label htmlFor="username">Username</label>
                    <input type="text" {...username} placeholder="Username" autoComplete="new-password"></input>
                </div>
                <div className="fullName" >
                    <label htmlFor="password">Password</label>
                    <input type="password" {...password} placeholder="Password" autoComplete="new-password"></input>
                </div>
                <input type="button" value={loading ? 'Loading...' : 'Login'} onClick={handleLogin} />Login
            </div>
        </div>
    );
}
export default Login;
