import React, { Component } from 'react';
import { Divider } from 'semantic-ui-react';


class Form extends Component {
    constructor(props) {
        super(props)

        this.state = {
            firstName: "",
            lastName: "",
            userName: "",
            password: ""
        }

        this.handleSubmit = this.handleSubmits.bind(this)
    }

    firsthandler = (event) => {
        this.setState({
            firstName: event.target.value
        })
    }

    lasthandler = (event) => {
        this.setState({
            lastName: event.target.value
        })
    }

    userhandler = (event) => {
        this.setState({
            userName: event.target.value
        })
    }

    passwordhandler = (event) => {
        this.setState({
            password: event.target.value
        })
    }


    handleSubmit = (event) => {
        alert(
            `${this.state.firstName} ${this.state.lastName} ${this.state.userName}  Registered Successfuly !!`)
        console.log(this.state);
        this.setState({
            firstName: "",
            lastName: "",
            userName: "",
            password: ""
        })

        event.preventDefault()
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit} >
                    <h1>User Registration</h1>
                    <label>FirstName :</label><input type="text" value={this.state.firstName} onChange={this.firstName}></input>
                    <label>LastName :</label> <input type="text" value={this.state.lastName} onChange={this.lastName}></input>
                    <label>UserName :</label> <input type="text" value={this.state.userName} onChange={this.userName}></input>
                    <label>Password :</label> <input type="password" value={this.state.password} onChange={this.password0}></input>

                    <input type="submit" value="Submit" />
                </form>

            </div>
        )
    }




} export default RegisterForm;
