import React, { Component, useEffect, useState } from "react";
import SalonsForm from "../components/SalonsForm";
import SalonCardComponent from "../components/SalonCardComponent";
import { Link, useParams, withRouter } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";



const Salons = () => {

  const [salons, setSalons] = useState();
  const [isLoaded, setIsLoaded] = useState();
  const params = useParams();

  console.log(params.id);

  useEffect(() => {
    if (!params.id) {
      fetch('http://localhost:3001/salons')
        .then(Response => Response.json())
        .then(salons => {
          setSalons(salons);
          setIsLoaded(true);
        })
    } else {
      fetch(`http://localhost:3001/salons/${params.id}`)
        .then(Response => Response.json())
        .then(salons => {
          setSalons(salons);
          setIsLoaded(true);
        })
    }
  }, []);

  if (!isLoaded) {
    return (
      <h1 >Loading...</h1>
    )
  } else {
    return (
      <div>

        <Header />
        <div className="container">
          {
            salons && salons.map(salon => (
              <SalonCardComponent key={salon.id} location={salon.location} name={salon.name} description={salon.description} />
            ))
          }
        </div>
        <Footer />
      </div>

    );
  }

}
export default withRouter(Salons);
