import React, { useEffect, useState } from "react";
// import { Form, Button, Field, Checkbox } from "semantic-ui-react";
import Salons from "../pages/Salons";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams
} from "react-router-dom";


const ReservationFormComponent = () => {
  const [locations, setLocations] = useState(null);
  const [services, setServices] = useState(null);

  console.log(locations, services);

  useEffect(() => {
    fetch('http://localhost:3306/location')
      .then(Response => Response.json())
      .then(locations => setLocations(locations))
  }, [locations]);

  useEffect(() => {
    fetch('http://localhost:3306/salon/service')
      .then(Response => Response.json())
      .then(services => setServices(services))
  }, [services]);

  const handleSubmit = event => {
    console.log(
      `${locations} , ${services}`
    )
    event.preventDefault();
  }

  return (
    <form className="reservation-form" onSubmit={handleSubmit}>
      <Switch>
        <Route path="Salons/:id" children={<Salons />} />
      </Switch>
      <div className="flex align-center  justify-between tabs">
        <span className="flex flex-column justify-center">
          <i class="fa fa-map-marker" aria-hidden="true"></i>
          <span className="">Treatments</span>
        </span>

        <span className="flex flex-column justify-center">
          <i class="fa fa-map-marker" aria-hidden="true"></i>
          <Link className="" to="/Salons">Salons</Link>
        </span>
      </div>
      <div className="form">

        <div className="drop-down">
          <select >{
            services && services.map(service => <option value="service.name">{service.name}</option>)
          }</select>
        </div>
        <div className="drop-down">
          <select>{
            locations && locations.map(location => <option value="location.location">{location.location}</option>)
          }</select>
        </div>
        <Link to={`salons/`} className="btn btn-submit">FIND ON BEAUTY DATE</Link>
      </div>
    </form>
  );
}
export default ReservationFormComponent;
