import React, { Component } from "react";
import { Form, Button, Field, Checkbox } from "semantic-ui-react";
import { warn } from "react";
import { Link } from "react-router-dom";


class ReservationFormComponent extends React.Component {
  constructor() {
    super()
    this.state = {
      salons: [],
      services: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentDidMount() {
    fetch('http://localhost:3001/salons')
      .then(Response => Response.json())
      .then(salons => this.setState({ salons: salons }))
    fetch('http://localhost:3001/salon/service')
      .then(Response => Response.json())
      .then(services => this.setState({ services: services }))
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form className="reservation-form" onSubmit={this.handleSubmit}>
        <div className="flex align-center  justify-between tabs">
          <span className="flex flex-column justify-center">
            <i class="fa fa-map-marker" aria-hidden="true"></i>
            <span className="">Treatments</span>
          </span>

          <span className="flex flex-column justify-center">
            <i class="fa fa-map-marker" aria-hidden="true"></i>
            <Link className="" to="/Salons">Salons</Link>
          </span>
        </div>
        <div className="form">
          <div className="drop-down">
            <select>{
              this.state.salons.map(salon => <option value="salon.value">{salon.name}</option>)
            }</select>
          </div>
          <div className="drop-down">
            <select>{
              // this.state.users.map(user => <option value="user.value">{user.name}</option>)
            }</select>
          </div>
          <input type="date" placeholder="" />
          <button className="btn btn-submit" type="submit">FIND ON BEAUTY DATE</button>
        </div>
      </form>
    );
  }

}
export default ReservationFormComponent;
