import React, { Component } from "react";
import { Form, Button, Field, Checkbox } from "semantic-ui-react";
import Salons from "../pages/Salons";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams
} from "react-router-dom";


class ReservationFormComponent extends React.Component {
  constructor() {
    super()
    this.state = {
      locations: [],
      services: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentDidMount() {
    fetch('http://localhost:3001/location')
      .then(Response => Response.json())
      .then(locations => this.setState({ locations: locations }))
    fetch('http://localhost:3001/salon/service')
      .then(Response => Response.json())
      .then(services => this.setState({ services: services }))
    console.log(this.state.services);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    console.log(
      `${this.state.location.location} , ${this.state.service.name}`
    )
    event.preventDefault();
  }

  render() {
    return (
      <form className="reservation-form" onSubmit={this.handleSubmit}>
        <Switch>
          <Route path="Salons/:id" children={<Salons />} />
        </Switch>
        <div className="flex align-center  justify-between tabs">
          <span className="flex flex-column justify-center">
            <i class="fa fa-map-marker" aria-hidden="true"></i>
            <span className="">Treatments</span>
          </span>

          <span className="flex flex-column justify-center">
            <i class="fa fa-map-marker" aria-hidden="true"></i>
            <Link className="" to="/Salons">Salons</Link>
          </span>
        </div>
        <div className="form">

          <div className="drop-down">
            <select >{
              this.state.services && this.state.services.map(service => <option value="service.name">{service.name}</option>)
            }</select>
          </div>
          <div className="drop-down">
            <select>{
              this.state.locations && this.state.locations.map(location => <option value="location.location">{location.location}</option>)
            }</select>
          </div>
          <Link to={'/sdsd'} className="btn btn-submit">FIND ON BEAUTY DATE</Link>
        </div>
      </form>
    );
  }

}
export default ReservationFormComponent;
