import React, { Component } from "react";
import { Form, Button, Field, Checkbox } from "semantic-ui-react";
import { warn } from "react";
import { Link } from "react-router-dom";


class ReservationFormComponent extends React.Component {
  constructor() {
    super()
    this.state = {
      users: [
      ]
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentDidMount() {
    fetch('http://jsonplaceholder.typicode.com/users')
      .then(Response => Response.json())
      .then(users => this.setState({ users: users }))
    fetch('http://jsonplaceholder.typicode.com/users')
      .then(Response => Response.json())
      .then(users => this.setState({ users: users }))
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form className="reservation-form" onSubmit={this.handleSubmit}>
        <div className="flex align-center">
          <span className="btn">Treatments</span>
          <Link className="" to="/Salons">Salons</Link>
        </div>
        <div className="drop-down">
          <select>{
            this.state.users.map(user => <option value="user.value">{user.name}</option>)
          }</select>
        </div>
        <div className="drop-down">
          <select>{
            this.state.users.map(user => <option value="user.value">{user.name}</option>)
          }</select>
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </div>
        <input type="date" placeholder="" />
        <button class="btn2" type="submit">FIND ON BEAUTY DATE</button>
      </form>
    );
  }

}
export default ReservationFormComponent;
