import React, { Component } from "react";
import SalonsForm from "../components/SalonsForm";
import SalonCardComponent from "../components/SalonCardComponent";
import { Link } from "react-router-dom";


class Salons extends React.Component {

  constructor() {
    super();
    this.state = {
      salons: []
    }
  }
  componentDidMount() {
    fetch('https://cors-anywhere.herokuapp.com/http://localhost:3001/reservations', {
      method: 'GET',
      headers: {
        'X-Requested-With': 'XMLHttpRequest'
      }
    })
      .then(Response => Response.json())
      .then(salons => this.setState({ isLoaded: true, salons: salons }))
  }
  render() {
    var { isLoaded, salons } = this.state;
    if (!isLoaded) {
      return (
        <h1 class="logo">Loading...</h1>
      )
    } else {
      return (
        <div>
          <header id="main-header">
            <div className="">
              <h1 className="logo">BeautyDate</h1>
              <nav>
                <ul>
                  <div class="separator"></div>
                  <li>
                    <Link to="/Home" >Home</Link>
                  </li>
                  <li>
                    <Link to="/Salons" >Salons</Link>
                  </li>

                  <li>
                    <Link to="/Blog" >Blog</Link>
                  </li>
                </ul>
              </nav>
            </div>
          </header>
          <div className="container">
            {
              this.state.salons.map(salon => (
                <SalonCardComponent id={salon.companyname} name={salon.reservationdate} address={salon.companylocation} suite={salon.status} />
              ))
            }
          </div>
        </div>

      );
    }

  }
}
export default Salons;
