import React, { Component } from "react";
import SalonsForm from "../components/SalonsForm";
import SalonCardComponent from "../components/SalonCardComponent";
import { Link, useParams } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";



class Salons extends React.Component {
  constructor() {
    const params = useParams();
    super();
    this.state = {
      salons: []
    }
    console.log(params);
  }
  componentDidMount() {
    fetch('http://localhost:3001/salons')
      .then(Response => Response.json())
      .then(salons => this.setState({ isLoaded: true, salons: salons }))
  }
  render() {
    var { isLoaded } = this.state;
    if (!isLoaded) {
      return (
        <h1 >Loading...</h1>
      )
    } else {
      return (
        <div>

          <Header />
          <div className="container">
            {
              this.state.salons.map(salon => (
                <SalonCardComponent key={salon.id} location={salon.location} name={salon.name} description={salon.description} />
              ))
            }
          </div>
          <Footer />
        </div>

      );
    }

  }
}
export default Salons;
