import React, { Component } from "react";
import SalonsForm from "../components/SalonsForm";
import SalonCardComponent from "../components/SalonCardComponent";
import { Link } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";



class Salons extends React.Component {

  constructor() {
    super();
    this.state = {
      salons: []
    }
  }
  componentDidMount() {
    fetch('http://localhost:3001/reservations')
      .then(Response => Response.json())
      .then(salons => this.setState({ isLoaded: true, salons: salons }))
  }
  render() {
    var { isLoaded } = this.state;
    if (!isLoaded) {
      return (
        <h1 >Loading...</h1>
      )
    } else {
      return (
        <div>

          <Header />
          <div className="container">
            {
              this.state.salons.map(salon => (
                <SalonCardComponent key={salon.id} id={salon.companyname} name={salon.reservationdate} address={salon.companylocation} suite={salon.status} />
              ))
            }
          </div>
          <Footer />
        </div>

      );
    }

  }
}
export default Salons;
