import React, { Component, useEffect } from "react";
import SalonsForm from "../components/SalonsForm";
import SalonCardComponent from "../components/SalonCardComponent";
import { Link, withRouter } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";



const Salons = () => {

  useEffect(() => {
    fetch('http://localhost:3001/salons')
      .then(Response => Response.json())
      .then(salons => this.setState({ isLoaded: true, salons: salons }))

  }, []);
  let { isLoaded } = this.state;
  if (!isLoaded) {
    return (
      <h1 >Loading...</h1>
    )
  } else {
    return (
      <div>

        <Header />
        <div className="container">
          {
            this.state.salons.map(salon => (
              <SalonCardComponent key={salon.id} location={salon.location} name={salon.name} description={salon.description} />
            ))
          }
        </div>
        <Footer />
      </div>

    );
  }

}
export default withRouter(Salons);
